package main

import "fmt"

// Simple function
func greet(name string) {
    fmt.Printf("Hello, %s!
", name)
}

// Function with return value
func add(a, b int) int {
    return a + b
}

func main() {
    greet("Alice")          // Output: Hello, Alice!
    result := add(3, 5)     // Adds 3 and 5
    fmt.Println("Sum:", result) // Output: Sum: 8
}

