package main

import (
	"context"
	"fmt"
	"os"

	"github.com/algorand/go-algorand-sdk/client/v2/algod"
)

func main() {
	// Algorand testnet node information
	algodToken := "" // Public node access
	algodServer := "https://testnet-api.algonode.cloud"

	// Create an algod client
	algodClient, err := algod.MakeClient(algodServer, algodToken)
	if err != nil {
		fmt.Fprintf(os.Stderr, "Failed to create algod client: %s
", err)
		return
	}

	// Fetch and print node status
	status, err := algodClient.Status().Do(context.Background())
	if err != nil {
		fmt.Fprintf(os.Stderr, "Failed to fetch node status: %s
", err)
		return
	}

	// Access and print the last round
	fmt.Printf("Last round: %d
", status.LastRound)
}

package main

import (
	"context"
	"fmt"
	"github.com/algorand/go-algorand-sdk/client/v2/algod"
	"github.com/algorand/go-algorand-sdk/crypto"
	"github.com/algorand/go-algorand-sdk/future"
)

func main() {
	// Testnet algod client setup
	algodToken := ""
	algodServer := "https://testnet-api.algonode.cloud"
	algodClient, err := algod.MakeClient(algodServer, algodToken)
	if err != nil {
		fmt.Printf("Failed to create algod client: %s
", err)
		return
	}

	// Sender account details
	senderAddress := "YOUR_SENDER_ADDRESS"
	privateKey := "YOUR_PRIVATE_KEY" // Private key of the sender

	// Receiver account details
	receiverAddress := "RECEIVER_ADDRESS"

	// Transaction amount in microAlgos
	amount := uint64(1000000) // 1 Algo = 1,000,000 microAlgos

	// Get suggested transaction parameters
	txParams, err := algodClient.SuggestedParams().Do(context.Background())
	if err != nil {
		fmt.Printf("Failed to get suggested transaction params: %s
", err)
		return
	}

	// Create a payment transaction
	note := []byte("Test transaction")
	tx, err := future.MakePaymentTxn(senderAddress, receiverAddress, amount, note, "", txParams)
	if err != nil {
		fmt.Printf("Failed to create payment transaction: %s
", err)
		return
	}

	// Sign the transaction
	signedTx, err := crypto.SignTransaction(privateKey, tx)
	if err != nil {
		fmt.Printf("Failed to sign transaction: %s
", err)
		return
	}

	// Send the transaction
	sendResponse, err := algodClient.SendRawTransaction(signedTx).Do(context.Background())
	if err != nil {
		fmt.Printf("Failed to send transaction: %s
", err)
		return
	}

	fmt.Printf("Transaction sent successfully! TxID: %s
", sendResponse)
}
